    private void createActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_createActionPerformed
        // TODO add your handling code here:;
        try{
            int n = Integer.parseInt(JOptionPane.showInputDialog("Tama√±o del input"));
            age = new int[n];
            for(int i = 0; i<n; i++){
               age[i] = Integer.parseInt(JOptionPane.showInputDialog("Edad de la posicion " + i));
            }
            JOptionPane.showMessageDialog(null, "Arreglo Lleno");
        }catch(NumberFormatException e){
            JOptionPane.showMessageDialog(null, "Error: "+e);
        }
    }//GEN-LAST:event_createActionPerformed

    private void getActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_getActionPerformed
        // TODO add your handling code here:
        try{
        int mayor = 0;
        int menor = 0;
        int sum1=0;
        int sum2=0;
        double prom = 0;

        int cantMa=0;
        int cantMe=0;
        for(int ag: age){
            JOptionPane.showMessageDialog(null, age.length);

            if(ag>=18){
                cantMa++;
                JOptionPane.showMessageDialog(null, "Mayor de edad " + ag);
                sum1++;
                mayor+=ag;
            }else{
                cantMe++;
                JOptionPane.showMessageDialog(null, "Menor de edad " + ag);
                sum2++;
                menor+=ag;

            }

        }
        //Suma de mayores
        prom = mayor/sum1;
        JOptionPane.showMessageDialog(null, "Hay " + sum1 + " personas mayores");
        JOptionPane.showMessageDialog(null, "Promedio de las personas mayores " + prom);
        //Suma de Menores
        prom = menor/sum2;
        JOptionPane.showMessageDialog(null, "Hay " + sum2 + " personas menores");
        JOptionPane.showMessageDialog(null, "Promedio de las personas menores " + prom);
        
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }

    }//GEN-LAST:event_getActionPerformed

